import React, { useState } from "react"
import CitiPicture from "./images/CitiPicture.png"
import charlotte from './images/charlotte.jpg'

const style = {
    main: {
        "backgroundImage": `url(${CitiPicture})`, "height": "100%",
        backgroundRepeat: "no-repeat",
        backgroundPosition: 'center',
        backgroundColor: 'rgb(74, 74, 74)',
        backgroundSize: 'cover'
    },
    jumbotron: {
        width: '50%',
        height: '60%',
        margin: 'auto auto',
        opacity: .9,
        backgroundSize: 'cover',
        fontSize: '1rem',
        fontWeight: 700
    }
}
const cityData = [
    {
        name: 'Charlotte',
        housing: 1056,
        costOfLiving: 977,
        avgIncome: 2653,
        image: charlotte
    }
]
const Home = () => {
    const [currCity, setCurrCity] = useState(0)
    const currentCity = cityData[currCity]
    return (
        <div style={style.main} className='d-flex align-items-center' >
            <div style={{ backgroundImage: `url(${currentCity.image})`, ...style.jumbotron }} className="jumbotron text-white text-center pt-0">
                <h1 className=' text-center'>{currentCity.name}</h1>
                <div style={{marginTop: '30%'}}>Average Cost of Living: ${currentCity.housing + currentCity.costOfLiving}</div>
                <div>Average Income: ${currentCity.avgIncome} </div>
                <div>Housing Cost {(currentCity.housing /currentCity.avgIncome)*100}% of Income </div>
            </div>
        </div >
    )
}

export default Home
