import React, { useState } from "react"
import CitiPicture from "./images/CitiPicture.png"
import charlotte from './images/charlotte.jpg'

const style = {
    main:  {
        "backgroundImage": `url(${CitiPicture})`, "height": "100%",
        backgroundRepeat: "no-repeat",
        backgroundPosition: 'center',
        backgroundColor: 'rgb(74, 74, 74)',
        backgroundSize: 'cover'
    },
    jumbotron:{
        width: '50%',
        height: '60%',
        margin: 'auto auto',
        opacity: 1,
        backgroundSize: 'cover'
    }
}
const cityData=[
    {
        name: 'Charlotte',
        housing: 1056,
        costOfLiving: 977,
        avgIncome: 2653,
        image:charlotte
    }
]
const Home = () => {
    const [currCity, setCurrCity] = useState(0)
    return (
        <div style={style.main} className='d-flex align-items-center' >
            <div style={{backgroundImage:`url(${cityData[currCity].image})`,...style.jumbotron}} className="jumbotron">
    <h1 className='text-light'>{cityData[currCity].name}</h1>
                
            </div>
        </div >
    )
}

export default Home
